mapper.update:
UPDATE product
prod_name=#{prodName}, quantity=#{quantity}, prod_detail=#{prodDetail:VARCHAR}, manufacture_day=to_date(#{manuDate:VARCHAR}), price=#{price:NUMERIC}, image_file=#{fileName:VARCHAR}
WHERE prod_no = #{prodNo}

mapper.sql:
SELECT prod_name, p.prod_no, prod_detail, manufacture_day, price, image_file, reg_date, TRAN_STATUS_CODE, p.quantity FROM product p, transaction t
p.prod_no = t.prod_no(+)
AND p.prod_no = #{searchKeyword}
AND prod_name LIKE '%'||#{searchKeyword}||'%'
AND price= #{searchKeyword}
ORDER BY p.prod_no ORDER BY price DESC NULLS LAST ORDER BY price NULLS FIRST

mapper.select:
  		SELECT *
  		FROM (	SELECT inner_table.*, ROWNUM AS row_seq
  				FROM (
  						
		  					 ) inner_table
		  		WHERE ROWNUM <= #{endRowNum} )
		WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
